import React from "react";
import { BrowserRouter as Router, Route, Routes } from "react-router-dom";
import "./App.css";

// Context
import { UserProvider } from "./UserContext";
import ProtectedRoute from "./ProtectedRoute";

// Components
import Admin from "./components/admin.js";
import Customer from "./components/customer.js";
import CreateAccount from "./components/createAccount.js";
import Login from "./components/login.js";
import EmployeePage from "./components/employee.js";

const App = () => {
  return (
<UserProvider>
<Router>
<div className="container">
<Routes>
<Route path="/admin" element={
<ProtectedRoute allowedRoles={['Administrator']}>
<Admin />
</ProtectedRoute>
            } />
<Route path="/customer" element={
<ProtectedRoute allowedRoles={['Customer', 'Employee', 'Administrator']}>
<Customer />
</ProtectedRoute>
            } />
<Route path="/createAccount" element={<ProtectedRoute allowedRoles={['Employee', 'Administrator']}>
 <CreateAccount /> </ProtectedRoute>} />
<Route path="/login" element={<Login />} />
<Route path="/employee" element={
<ProtectedRoute allowedRoles={['Employee', 'Administrator']}>
<EmployeePage />
</ProtectedRoute>
            } />
</Routes>
</div>
</Router>
</UserProvider>
  );
}

export default App;
