import React, { useState } from "react";
import "bootstrap/dist/css/bootstrap.min.css";
import "./employee.css"

const EmployeePage = () => {
  const [customerId, setCustomerId] = useState("");
  const [amount, setAmount] = useState("");
  const [targetCustomerId, setTargetCustomerId] = useState("");
  const [accountDetails, setAccountDetails] = useState(null);

  const handleViewAccount = () => {
    // Dummy
    setAccountDetails({
      savings: 5000,
      checking: 2000,
      investment: 15000,
      transactions: [
        {
          date: "2024-07-01",
          type: "Deposit",
          amount: 1000,
          account: "savings"
        },
        {
          date: "2024-07-02",
          type: "Withdrawal",
          amount: 500,
          account: "checking"
        },
        {
          date: "2024-07-03",
          type: "Transfer",
          amount: 2000,
          account: "investment"
        }
      ]
    });
  };

  const handleDeposit = () => {
    // Dummy deposit logic
    alert(`Deposited $${amount} into ${customerId}'s account`);
  };

  const handleWithdraw = () => {
    // Dummy withdrawal logic
    alert(`Withdrew $${amount} from ${customerId}'s account`);
  };

  const handleTransfer = () => {
    // Dummy transfer logic
    alert(`Transferred $${amount} from ${customerId} to ${targetCustomerId}`);
  };

  return (
    <div className="container mt-5">
      <div className="employee-container p-4">
        <h2>Employee Dashboard</h2>
        <div className="form-group">
          <label>Customer ID</label>
          <input
            type="text"
            className="form-control"
            value={customerId}
            onChange={(e) => setCustomerId(e.target.value)}
          />
        </div>
        <button className="btn btn-primary mb-3" onClick={handleViewAccount}>
          View Account
        </button>
        {accountDetails && (
          <div className="account-details">
            <h4>Account Details</h4>
            <p>Savings: ${accountDetails.savings}</p>
            <p>Checking: ${accountDetails.checking}</p>
            <p>Investment: ${accountDetails.investment}</p>
            <h4>Transactions</h4>
            <ul>
              {accountDetails.transactions.map((txn, index) => (
                <li key={index}>
                  {txn.date} - {txn.type} of ${txn.amount} in {txn.account}
                </li>
              ))}
            </ul>
          </div>
        )}
        <div className="form-group">
          <label>Amount</label>
          <input
            type="number"
            className="form-control"
            value={amount}
            onChange={(e) => setAmount(e.target.value)}
          />
        </div>
        <button className="btn btn-success mb-3" onClick={handleDeposit}>
          Deposit
        </button>
        <button className="btn btn-warning mb-3" onClick={handleWithdraw}>
          Withdraw
        </button>
        <div className="form-group">
          <label>Target Customer ID</label>
          <input
            type="text"
            className="form-control"
            value={targetCustomerId}
            onChange={(e) => setTargetCustomerId(e.target.value)}
          />
        </div>
        <button className="btn btn-info" onClick={handleTransfer}>
          Transfer
        </button>
      </div>
    </div>
  );
};

export default EmployeePage;
