import React, { useEffect, useState } from "react";
import  { useNavigate } from "react-router";
import "./admin.css"


export default function AccountInfo () {
    // Variables used to store the user information from the backend
    const [form, setForm] = useState("");
    const [firstName, setFirstName] = useState();
    const [lastName, setLastName] = useState();


    // Used to navigate to a new page
    const navigate = useNavigate();

    useEffect(() => {
        // Checks if the user is logged in
        async function getAdmin() {
            const isAdmin = await fetch("http://localhost:4000/testAdmin", // TO DO: get user session and get if they are an admin
                {
                    method: "GET",
                    credentials: "include"
                }
            );
            if(!isAdmin.ok){ // Gets back an ok if the person is admin
                // If not signed in the user gets sent to the login page
                navigate("/"); // navigate to home page

            } else {
                const admin = await isAdmin.json();
                if(admin.role === "Administrator"){
                    // If the user is admin
                    // A request is sent to the backend to get the user information
                    setFirstName(admin.firstName);
                    setLastName(admin.lastName);
                } else {
                    // if not go back to home page
                    navigate("/");
                }
            }
        }
        getAdmin();

        return;
    },[]); 

    // Used to update the Json object from the form
    function updateForm(jsonObj){
        return setForm((prevJsonObj) => {
            return {...prevJsonObj, ...jsonObj};
        });
    }


    async function onSubmit(e) {
        let response;
        e.preventDefault();

        // Json object for the login information
        const updatedUser = {
            ...form
        };
        // sends a request to the backend to login the usr
        response = await fetch(`http://localhost:4000/testUpdateUser`, { // TO DO make route to save updated information
            method: "POST",
            headers: {
                "Content-Type" : "application/json",
            },
            body: JSON.stringify(updatedUser),
        })
        .catch(error => {
            // If the request has an issue
            window.alert(error);
            return;
        });
        // If the user was successfully logged in
        if(response.status === 200){
           window.alert("User role as been updated")
        } else {
            // If the user was information was incorrect
            window.alert("Not a valid user");
        }

    }

    // HTML page
    return (
    <div className="admin-container">
        <h3>Update Role</h3>
        <p>Hello {firstName} {lastName}</p>
        <p>What user would you like to update?</p>
        <form onSubmit={onSubmit}>
            <div>
                <label>User: </label>
                <input
                    type="text"
                    id="user"
                    value={form.user}
                    onChange={(e) => updateForm({ user: e.target.value})}
                />
            </div>
            <div>
                <div>
                <label>Role: </label>
                <input 
                        type="radio" 
                        id="Customer" 
                        value= "Customer" 
                        name="role"
                        onChange={(e) => updateForm({role : e.target.value})}/>
                    <label for="Customer">Customer   </label>
                <input 
                        type="radio" 
                        id="Administrator" 
                        value= "Administrator" 
                        name="role"
                        onChange={(e) => updateForm({role : e.target.value})}/>
                    <label for="Administrator">Administrator   </label>
                <input 
                        type="radio" 
                        id="Employee" 
                        value= "Employee" 
                        name="role"
                        onChange={(e) => updateForm({role : e.target.value})}/>
                    <label for="Employee">Employee</label>
                </div>
            </div>
            <br/>
            <div>
                <input
                    type="submit"
                    value="Update Role"
                />
            </div>
        </form>
    </div>
    );
}
