import React from 'react';

import { BrowserRouter as Router, Route, Routes, Navigate } from 'react-router-dom';

import Login from './Login';

import AdminPage from './AdminPage';

import CustomerPage from './CustomerPage';

import EmployeePage from './EmployeePage';

const ProtectedRoute = ({ children, role, userRole }) => {

    if (userRole !== role) {

        return <Navigate to="/" />;

    }

    return children;

};

const App = () => {

    const [userRole, setUserRole] = useState(null);

    // Assume userRole is set somewhere after login

    // setUserRole('Administrator'); // Example of setting role

    return (

        <Router>

            <Routes>

                <Route path="/" element={<Login />} />

                <Route path="/admin" element={

                    <ProtectedRoute role="Administrator" userRole={userRole}>

                        <AdminPage />

                    </ProtectedRoute>

                } />

                <Route path="/customer" element={

                    <ProtectedRoute role="Customer" userRole={userRole}>

                        <CustomerPage />

                    </ProtectedRoute>

                } />

                <Route path="/employee" element={

                    <ProtectedRoute role="Employee" userRole={userRole}>

                        <EmployeePage />

                    </ProtectedRoute>

                } />

            </Routes>

        </Router>

    );

};

export default App;
