import React, { useState } from "react";
import { useNavigate } from "react-router";
import "./login.css";

export default function Login() {
    // Used to store the user login information
    const [form, setForm] = useState({ userName: "", password: "" });
    const navigate = useNavigate();

    // Used to update the JSON object from the form
    function updateForm(jsonObj) {
        setForm(prevJsonObj => ({ ...prevJsonObj, ...jsonObj }));
    }

    // After the user clicks the submit button
    // Checks with the backend to make sure the user exists and verifies their login
    async function onSubmit(e) {
        e.preventDefault();
        // JSON object for the login information
        const loginInfo = { userName: form.userName, password: form.password };

        try {
            const response = await fetch(`http://localhost:4000/testUpdateUser`, {
                method: "POST",
                headers: { "Content-Type": "application/json" },
                body: JSON.stringify(loginInfo),
            });

            if (response.ok) {
                const user = await response.json();
                switch (user.role) {
                    case 'Administrator':
                        navigate("/admin");
                        break;
                    case 'Customer':
                        navigate("/customer");
                        break;
                    case 'Employee':
                        navigate("/employee");
                        break;
                    default:
                        window.alert("Invalid role");
                }
            } else {
                const error = await response.json();
                window.alert(error.message);
            }
        } catch (error) {
            window.alert("An error occurred: " + error.message);
        }
    }

    return (
<div className="login-container">
<h3 className="login-title">Login</h3>
<form onSubmit={onSubmit}>
<div>
<label className="login-label">User Name: </label>
<input
                        type="text"
                        id="userName"
                        className="login-input"
                        value={form.userName}
                        onChange={(e) => updateForm({ userName: e.target.value })}
                    />
</div>
<div>
<label className="login-label">Password: </label>
<input
                        type="password"
                        id="password"
                        className="login-input"
                        value={form.password}
                        onChange={(e) => updateForm({ password: e.target.value })}
                    />
</div>
<br />
<div>
<input
                        type="submit"
                        className="login-submit"
                        value="Login"
                    />
</div>
</form>
</div>
    );
}
