import React, { useState, useEffect } from "react";
import axios from "axios";

const Game = ({ username }) => {
  const [word, setWord] = useState("");
  const [input, setInput] = useState("");
  const [numOfGuesses, setNumOfGuesses] = useState(0);
  const [wrongGuesses, setWrongGuesses] = useState(0);
  const [message, setMessage] = useState("");
  const [guessedLetters, setGuessedLetters] = useState([]);

  useEffect(() => {
    fetchWord();
  }, []);

  const fetchWord = async () => {
    try {
      const response = await axios.get("http://localhost:4000/word");
      setWord(response.data.word);
      setNumOfGuesses(0);
      setWrongGuesses(0);
      setMessage("");
      setGuessedLetters([]);
    } catch (error) {
      console.error("Error fetching word:", error);
      setMessage("Error fetching word. Please try again.");
    }
  };

  const handleGuess = async () => {
    if (!input) return; // Avoid processing if input is empty

    try {
      const response = await axios.post("http://localhost:4000/guess", { guess: input });

      if (response.status === 200 || response.status === 250) {
        setWord(response.data.word);
        setNumOfGuesses(response.data.numOfGuesses);
        setWrongGuesses(response.data.wrongGuesses);
        setMessage(response.status === 200 ? "Good guess!" : "Congratulations! You've guessed the word!");
      }
    } catch (error) {
      if (error.response) {
        const status = error.response.status;
        if (status === 400) {
          setMessage("Incorrect guess.");
          setWrongGuesses(error.response.data.wrongGuesses);
        } else if (status === 450) {
          setMessage("Letter already guessed.");
        } else if (status === 451) {
          setMessage("Game over!");
          setWord(error.response.data.word);
        } else {
          setMessage("An error occurred. Please try again.");
        }
      } else {
        setMessage("An error occurred. Please try again.");
      }
    } finally {
      setGuessedLetters((prev) => [...prev, input]);
      setInput("");
    }
  };

  const handleChange = (e) => {
    setInput(e.target.value);
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    handleGuess();
  };

  const resetGame = async () => {
    await axios.post("http://localhost:4000/reset");
    fetchWord();
    setInput("");
    setGuessedLetters([]);
    setNumOfGuesses(0);
    setWrongGuesses(0);
    setMessage("");
  };

  return (
<div className="App">
<h1>Hangman Game</h1>
<p>Welcome, {username}!</p>
<p>{word}</p>
<form onSubmit={handleSubmit}>
<input
          type="text"
          value={input}
          onChange={handleChange}
          maxLength="1"
          required
        />
<button type="submit">Guess</button>
</form>
<button onClick={resetGame}>Reset Game</button>
<p>Guesses: {numOfGuesses}</p>
<p>Wrong Guesses: {wrongGuesses}</p>
<p>Guessed Letters: {guessedLetters.join(", ")}</p>
<p>{message}</p>
</div>
  );
};

export default Game;
