import React, { useState, useEffect } from "react";
import axios from "axios";

const Gameboard = ({ username }) => {
  const [word, setWord] = useState("");
  const [input, setInput] = useState("");
  const [numOfGuesses, setNumOfGuesses] = useState(0);
  const [message, setMessage] = useState("");
  const [guessedLetters, setGuessedLetters] = useState([]);

  useEffect(() => {
    fetchWord();
  }, []);

  const fetchWord = async () => {
    try {
      const response = await axios.get("http://localhost:4000/word");
      setWord(response.data.word);
    } catch (error) {
      console.error("Error fetching word:", error);
    }
  };

  const handleGuess = async () => {
    try {
      const response = await axios.post("http://localhost:4000/guess", { guess: input });

      if (response.status === 200 || response.status === 250) {
        setWord(response.data.word);
        setNumOfGuesses(response.data.numOfGuesses);
        setMessage(response.status === 200 ? "Good guess!" : "Congratulations! You've guessed the word!");
      }
    } catch (error) {
      if (error.response) {
        // The request was made and the server responded with a status code that falls out of the range of 2xx
        const status = error.response.status;
        if (status === 400) {
          setMessage("Incorrect guess.");
        } else if (status === 450) {
          setMessage("Letter already guessed.");
        } else if (status === 451) {
          setMessage("Game over!");
          setWord(error.response.data.word); // Show the correct word if the game is over
        } else {
          setMessage("An error occurred. Please try again.");
        }
      } else {
        // Something happened in setting up the request that triggered an Error
        setMessage("An error occurred. Please try again.");
      }
      setNumOfGuesses(error.response?.data?.numOfGuesses || numOfGuesses);
    } finally {
      setGuessedLetters([...guessedLetters, input]);
      setInput("");
    }
  };

  const handleChange = (e) => {
    setInput(e.target.value);
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    handleGuess();
  };

  return (
<div className="App">
<h1>Hangman Game</h1>
<p>Welcome, {username}!</p>
<p>{word}</p>
<form onSubmit={handleSubmit}>
<input
          type="text"
          value={input}
          onChange={handleChange}
          maxLength="1"
          required
        />
<button type="submit">Guess</button>
</form>
<p>Guesses: {numOfGuesses}</p>
<p>Guessed Letters: {guessedLetters.join(", ")}</p>
<p>{message}</p>
</div>
  );
};

export default Gameboard;
